import display from './css/display.css';
import React from 'react';

function Display(props){

  const deleteDetailsItem = ((id, name, surname, position)=>{

 

    props.delete(id, name, surname, position)
  

  })

  return(
    <div className="container">


  
<div className="row">
          {props.data.map((addEmployee)=>(


           

              <div  className="card col-lg-2 mx-2 mt-5">
                <h1 className="add1">

                  {addEmployee.id}
                   {addEmployee.name}
                    {addEmployee.surname}
                     {addEmployee.position}

                </h1>
                  </div> 
                    
          ))}

</div>


<div>

 {props.data.map((deleteDetails)=>(

    <div>

    <h1>

        {deleteDetails.id}
         {deleteDetails.name}
          {deleteDetails.surname}
           {deleteDetails.details}


    </h1>

    <button onClick={()=>deleteDetailsItem(deleteDetails.id,deleteDetails.name,deleteDetails.surname,deleteDetails.position)}>Delete</button>

    </div>



   

))}



</div>
   
       </div>
 )

}



export default Display;
