                    </h4>
                      
                   
             
             

                
 {props.data.map((deleteDetail)=>(

<div>

<h1>

{deleteDetail.detail}{updateDetail.detail}

</h1>

<button onClick={()=>deleteDetailItem(deleteDetail.id,deleteDetail.name,deleteDetail.surname,deleteDetail.position)}>Delete</button>
<button onClick={()=>updateteDetailItem(updateDetail.id,updateDetail.name,updateteDetail.surname,updateteDetail.position)}>Update</button>

</div>





))}
                  </div> 
                    
          ))}

</div>






</div>
   
       
 )

}



